def remove_duplicate_categories(categories):
    """去重：根据familyName和url去重"""
    seen = set()
    unique_cats = []
    for cat in categories:
        key = (cat["familyName"], cat["url"])
        if key not in seen:
            seen.add(key)
            # 递归去重子分类
            if cat["children"]:
                cat["children"] = remove_duplicate_categories(cat["children"])
            unique_cats.append(cat)
    return unique_cats

def parse_category(element, current_level, parent_soup):
    """
    递归解析分类层级（修复版）：
    - 1级：提取name/url/data-uuid，通过data-uuid匹配二级
    - 2/3级：提取名称和URL，层级自动递增
    - 新增：增强调试日志、放宽选择器、优化名称提取
    """
    category = {
        "familyName": "",
        "level": current_level,
        "url": "",
        "data-uuid": "",
        "children": []
    }

    # ========== 修复1：优化一级分类解析逻辑 ==========
    if current_level == 1:
        a_tag = element.find("a", href=True)
        if a_tag:
            # 提取基础信息
            category["url"] = a_tag["href"].strip()
            category["data-uuid"] = a_tag.get("data-uuid", "").strip()

            # 修复：优化名称提取（兼容不同span结构）
            # 方式1：先取span的text，取不到则取a标签的text
            span_tag = a_tag.find("span", attrs={"aria-hidden": False})  # 排除箭头span
            if span_tag and span_tag.text.strip():
                category["familyName"] = span_tag.text.strip()
            else:
                # 兜底：直接取a标签的文本（清理空白）
                category["familyName"] = a_tag.text.strip()

            # 调试：打印一级分类基础信息
            # print(f"【一级分类】名称：{category['familyName']} | UUID：{category['data-uuid']} | URL：{category['url']}")

            # ========== 修复2：优化二级分类匹配逻辑 ==========
            if category["data-uuid"]:
                # 放宽选择器：去掉多余的限制，优先匹配包含data-uuid的items容器
                target_items = parent_soup.select_one(
                    f".products-submenu .items[data-uuid='{category['data-uuid']}']"
                )
                # 兜底匹配：如果上面没找到，扩大范围
                if not target_items:
                    target_items = parent_soup.select_one(f"[data-uuid='{category['data-uuid']}']")

                if target_items:
                    # ========== 核心修复：分层解析二级分类 ==========
                    # 步骤1：找到所有顶级的二级li（直接子节点，不是嵌套的）
                    # 方法：通过CSS选择器直接取target_items的直接子li.nav-item
                    sub_items = target_items.select("ul > li.nav-item")  # > 表示直接子节点，关键！

                    # 兜底：如果没有直接子li，再取所有li（兼容不同结构）
                    if not sub_items:
                        sub_items = target_items.find_all("li", class_="nav-item", recursive=False)

                    # print(f"  找到二级分类数量（去重前）：{len(sub_items)}")

                    # 解析每个二级li
                    for sub_item in sub_items:
                        child = parse_category(sub_item, 2, parent_soup)
                        if child["familyName"] and child["url"]:
                            category["children"].append(child)

                    # 去重二级分类
                    # category["children"] = remove_duplicate_categories(category["children"])
                    # print(f"  找到二级分类数量（去重后）：{len(category['children'])}")
                else:
                    print(f"  未找到UUID={category['data-uuid']}对应的二级分类容器")
            else:
                print(f"【一级分类】当前li无有效a标签：{element}")

    # ========== 修复3：优化二级/三级分类解析 ==========
    else:
        # 步骤1：排除Product Selector的a标签
        a_tag = element.find("a", href=True, class_="megamenu__link")

        # 步骤2：优先选有data-uuid/data-nid的a标签
        if not a_tag:
            a_tag = element.find("a", href=True, attrs={"data-uuid": True}) or element.find("a", href=True,
             attrs={"data-nid": True})

        # 步骤3：终极兜底 - 取最后一个a标签
        if not a_tag:
            all_a_tags = element.find_all("a", href=True)
            if all_a_tags:
                a_tag = all_a_tags[-1]

        if a_tag:
            category["url"] = a_tag["href"].strip()
            category["data-uuid"] = a_tag.get("data-uuid", "").strip()
            # 安全提取名称，避免None报错
            category_name = a_tag.string.strip() if a_tag.string else a_tag.text.strip()
            category["familyName"] = category_name.replace("Product Selector", "").strip()

            # 调试：打印选中的a标签信息
            # print(f"【{current_level}级分类】选中的a标签：{a_tag}")
            # print(f"【{current_level}级分类】最终名称：{category['familyName']} | URL：{category['url']}")

            # 解析三级分类
            third_level_container = element.find("ul") or element.find("ul", class_="sub-nav-group")
            if third_level_container:
                third_level_lis = third_level_container.find_all("li", class_="nav-item")
                # print(f"  找到{current_level + 1}级分类数量：{len(third_level_lis)}")

                for third_li in third_level_lis:
                    grand_child = parse_category(third_li, current_level + 1, parent_soup)
                    if grand_child["familyName"] and grand_child["url"]:
                        category["children"].append(grand_child)
        else:
            print(f"【{current_level}级分类】当前li无有效分类a标签：{element}")

    return category


def remove_duplicate_second_level(categories):
    """
    移除重复的二级分类（二级分类的familyName+url出现在三级分类中则判定为重复）
    :param categories: 原始分类列表（一级分类列表）
    :return: 处理后的分类列表，以及被删除的二级分类列表
    """
    # 步骤1：收集所有三级分类的 (familyName, url) 唯一标识，存入集合用于快速判断
    third_level_identifiers = set()
    for first_level in categories:
        # 遍历二级分类
        for second_level in first_level.get("children", []):
            # 遍历三级分类，收集标识
            for third_level in second_level.get("children", []):
                key = (third_level["familyName"], third_level["url"])
                third_level_identifiers.add(key)

    # 步骤2：遍历所有二级分类，筛选并删除重复项
    deleted_second_level = []  # 记录被删除的二级分类
    for first_level in categories:
        second_level_list = first_level.get("children", [])
        # 临时列表：存放保留的二级分类
        kept_second_level = []

        for second_level in second_level_list:
            # 生成当前二级分类的唯一标识
            second_key = (second_level["familyName"], second_level["url"])

            # 判断：如果二级分类的标识出现在三级分类集合中 → 重复，需要删除
            if second_key in third_level_identifiers:
                deleted_second_level.append({
                    "familyName": second_level["familyName"],
                    "url": second_level["url"],
                    "parent_familyName": first_level["familyName"]  # 记录所属一级分类，方便查看
                })
            else:
                # 不重复则保留
                kept_second_level.append(second_level)

        # 替换一级分类的children为筛选后的二级分类列表
        first_level["children"] = kept_second_level

    # 步骤3：打印被删除的二级分类信息
    print("===== 被删除的重复二级分类 =====")
    if not deleted_second_level:
        print("无重复的二级分类需要删除")
    else:
        for idx, deleted in enumerate(deleted_second_level, 1):
            print(f"{idx}. 所属一级分类: {deleted['parent_familyName']}")
            print(f"   二级分类名称: {deleted['familyName']}")
            print(f"   二级分类URL: {deleted['url']}")
            print("-" * 80)

    return categories, deleted_second_level


def find_leaf_nodes(processed_categories):
    # 存储最终的叶子节点结果
    leaf_nodes = []

    def recursive_traverse(nodes, current_path):
        """
        递归遍历分类节点

        参数:
            nodes: 当前层级的节点列表
            current_path: 记录当前分类层级的路径列表（如["电子产品", "手机"]）
        """
        for node in nodes:
            # 1. 获取当前节点的核心字段，做容错处理
            family_name = node.get("familyName", "")
            node_url = node.get("url", "")
            children = node.get("children", [])

            # 2. 将当前节点的名称加入层级路径
            current_path.append(family_name)

            # 3. 判断是否是叶子节点（children为空列表）
            if not children:
                # 拼接category：层级路径用^连接
                category = "^".join(current_path)
                # 拼接url：前缀 + 节点url + 后缀
                full_url = f"https://www.renesas.com{node_url}/product-selector"
                # 添加到结果列表
                leaf_nodes.append({
                    "url": full_url,
                    "category": category
                })
            else:
                # 非叶子节点，递归遍历下一级
                recursive_traverse(children, current_path)

            # 4. 回溯：移除当前节点的名称，处理同层级下一个节点
            current_path.pop()

    # 启动递归遍历（初始路径为空列表）
    recursive_traverse(processed_categories, [])

    return leaf_nodes

